Create your own searching logicimport {Pipe} from &apos;angular2/core&apos;;
@Pipe({
name: &apos;search&apos;
})
export class Search{
transform(data, key, term = &quot;&quot;){
  if(!data) return null;
  return data.filter(item =&gt; {
    return item[key]
      .toLowerCase()
      .includes(term.toLowerCase());
  })
}
}



