import {Component} from &apos;angular2/core&apos;;
import {Dumb} from &apos;./components/dumb.component&apos;;

@Component({
    selector: &apos;smart&apos;,
    directives: [Dumb],
    template: `
    &lt;dumb [message]=&quot;memo&quot; (open)=&quot;onOpen($event)&quot;&gt;&lt;/dumb&gt;    
`
})
export class Smart{
    onOpen(event){
        console.log(event);
    }

    memo = &quot;You&apos;ve got mail&quot;;
}


src/components/dumb.component.ts

import {Component, Output, Input, EventEmitter} from &apos;angular2/core&apos;;

@Component({
    selector: &apos;dumb&apos;,
    template: `
    &lt;button (click)=&quot;onClick()&quot;&gt;&lt;/button&gt;

`
})
export class Dumb{
    @Input() message;
    @Output() open = new EventEmitter();

    onClick(){
        this.open.emit(&apos;message opened!&apos;);
    }
}

What&apos;s That Syntax?
Field Assignment
Field assignment in classes is unique to TypeScript. This may one
day land in JavaScript proper, but for now is only a proposal.
class Example{
    //only works in TypeScript
    message = &quot;hi&quot;; 
}
