import {Component, Output, Input, EventEmitter} from &apos;angular2/core&apos;;

@Component({
    selector: &apos;dumb&apos;,
    template: `
    &lt;button (click)=&quot;onClick()&quot;&gt;&lt;/button&gt;

`
})
export class Dumb{
    @Input() message;
    @Output() open = new EventEmitter();

    onClick(){
        this.open.emit(&apos;message opened!&apos;);
    }
}

